mapper.select:
SELECT * FROM cook
  WHERE line_code LIKE CONCAT('%', #{line_code}, '%')

    AND co_date = #{co_date}




      AND co_status = #{co_status}


  AND so_code LIKE CONCAT('%', #{so_code}, '%')
  LIMIT #{startRow}, #{pageSize}

	SELECT count(*) FROM cook

	WHERE line_code LIKE CONCAT('%', #{line_code}, '%')

    AND co_date = #{co_date}




      AND co_status = #{co_status}


  AND so_code LIKE CONCAT('%', #{so_code}, '%')

SELECT * FROM cook_add
where co_code=#{co_code}
LIMIT #{startRow}, #{pageSize}

mapper.update:
UPDATE cook
SET cpQTY = cpQTY + #{cfQTY}
WHERE co_code = #{co_code}
AND #{cerf_gobd} = 'n'

mapper.select:
	 SELECT co_status
	 FROM cook
     WHERE co_code = #{co_code}

mapper.update:

update cook
set co_status = '시작'
where co_code = #{co_code}


update cook
set co_status = '마감'
where co_code = #{co_code}


update cook
set co_status = '지시'
where co_code = #{co_code}

UPDATE prod
SET pQTY = pQTY + #{pfQTY}
WHERE wo_code = #{wo_code}
AND #{perf_gobd} = 'n'

UPDATE cook
SET cpQTY = cpQTY - #{cfQTY}
WHERE co_code = #{co_code}

mapper.select:
select count(cook_id)
from cook_add
where co_code=#{co_code}

SELECT
  CASE
    WHEN (
      SELECT SUM(
        CASE
          WHEN cpQTY >= material_con THEN 1
          ELSE 0
        END
      )
      FROM cook
      WHERE so_code = #{so_code}
    ) = (
      SELECT COUNT(*)
      FROM cook
      WHERE so_code = #{so_code}
    ) THEN 1
    ELSE 0
  END AS result
FROM dual;
