mapper.select:
SELECT material_code, material_con FROM item_recipe
where
item_code=#{item_code}

select material_code, material_con from item_recipe
where
item_code=#{item_code}

mapper.update:
update item_recipe
set item_code=#{item_code},
material_code=#{material_code}, material_con=#{material_con}

mapper.delete:
delete from item_recipe
where item_code=#{item_code}
and material_code=#{material_code}
