mapper.select:
SELECT ppa.item_code, SUM(ppa.pfQTY) AS total_QTY, SUM(CASE WHEN ppa.perf_gobd = 'Y' THEN ppa.pfQTY ELSE 0 END) AS total_error_QTY, ie.error_status FROM prod p LEFT OUTER JOIN prod_perf_add ppa ON p.wo_code = ppa.wo_code LEFT OUTER JOIN items_error_rate ie ON ie.item_code = ppa.item_code WHERE ppa.item_code IS NOT NULL AND ppa.item_code LIKE CONCAT('%', #{item_code}, '%')
AND ie.error_status LIKE CONCAT('%', #{error_status}, '%')
GROUP BY ppa.item_code, ie.error_status ORDER BY ppa.item_code asc LIMIT #{startRow}, #{pageSize}
