mapper.select:
  		select out_num, customer_code, item_name, standard, out_qty, stock_code, due_date, out_state, out_date, employee_id
		from outbound
		limit #{startRow}, #{pageSize}

mapper.update:
update outbound
set out_state = #{out_state}
where out_num = #{out_num}

mapper.select:
  		select item_code, item_name, standard, out_qty, due_date, out_notes
		from outbound
		where out_num=#{out_num}
