mapper.select:
SELECT DISTINCT p.wo_code, p.line_code, p.item_code, p.sQTY, p.pQTY, p.item_name, ir.employee_name, ( SELECT COALESCE(SUM(pfQTY), 0) FROM prod_perf_add WHERE perf_gobd = 'y' AND wo_code = p.wo_code ) + COALESCE(ir.total_error_QTY, 0) as total_error_QTY, p.line_name FROM prod p LEFT OUTER JOIN prod_perf_add pa ON pa.wo_code = p.wo_code LEFT OUTER JOIN items_error_rate ir ON ir.wo_code = p.wo_code WHERE p.wo_code LIKE CONCAT('%', #{wo_code}, '%') AND p.line_code LIKE CONCAT('%', #{line_code}, '%') AND p.item_name LIKE CONCAT('%', #{item_name}, '%')
AND ir.employee_name LIKE CONCAT('%', #{employee_name}, '%')
LIMIT #{startRow}, #{pageSize}

select p.wo_code, pp.item_code, SUM(pp.pfQTY) AS total_QTY, SUM(CASE WHEN pp.perf_gobd = 'Y' THEN pp.pfQTY ELSE 0 END) AS total_error_QTY, ie.error_status from prod p LEFT OUTER JOIN prod_perf_add pp ON p.wo_code = pp.wo_code LEFT OUTER JOIN items_error_rate ie ON pp.wo_code = ie.wo_code WHERE pp.wo_code = #{wo_code}
and pp.item_code LIKE CONCAT('%', #{item_code}, '%')
and ie.error_status LIKE CONCAT('%', #{error_status}, '%')
AND pp.item_code is not null LIMIT #{startRow}, #{pageSize}

mapper.update:
update items_error_rate
set error_status = #{error_status}, item_code = #{item_code}
where wo_code = #{wo_code}
